import{_ as i,c as r,o as t,ag as n}from"./chunks/framework.B6PdRehI.js";const u=JSON.parse('{"title":"Core System","description":"","frontmatter":{"title":"Core System","category":"Core"},"headers":[],"relativePath":"core/index.md","filePath":"core/index.md"}'),s={name:"core/index.md"};function a(o,e,l,h,d,c){return t(),r("div",null,e[0]||(e[0]=[n('<h1 id="core-system" tabindex="-1">Core System <a class="header-anchor" href="#core-system" aria-label="Permalink to &quot;Core System&quot;">​</a></h1><p>Das Core System der Casoon UI Library bildet das Fundament für alle Komponenten und Stile. Es definiert die grundlegenden Prinzipien und Strukturen, auf denen das gesamte Design-System aufbaut.</p><h2 id="ubersicht" tabindex="-1">Übersicht <a class="header-anchor" href="#ubersicht" aria-label="Permalink to &quot;Übersicht&quot;">​</a></h2><p>Das Core System besteht aus mehreren Schlüsselkomponenten:</p><ul><li><strong>Layer System</strong>: Organisation der CSS-Schichten für optimale Spezifität und Wartbarkeit</li><li><strong>CSS-Architektur</strong>: Strukturierung und Organisation des CSS-Codes</li><li><strong>Color System</strong>: Farbsystem und -management</li><li><strong>Color-Mix System</strong>: Fortschrittliche Farbmischung und -manipulation</li><li><strong>Logical Properties</strong>: Verwendung von logischen Eigenschaften für sprachunabhängiges Layout</li><li><strong>Smooth Scrolling</strong>: Implementierung von weichem Scrollen für bessere Benutzererfahrung</li></ul><h2 id="philosophie" tabindex="-1">Philosophie <a class="header-anchor" href="#philosophie" aria-label="Permalink to &quot;Philosophie&quot;">​</a></h2><p>Das Core System folgt diesen Designprinzipien:</p><ol><li><strong>Modularität</strong>: Unabhängige, wiederverwendbare Komponenten</li><li><strong>Konsistenz</strong>: Einheitliche Designsprache</li><li><strong>Flexibilität</strong>: Anpassbarkeit an verschiedene Anforderungen</li><li><strong>Performance</strong>: Optimierte Leistung bei minimaler Dateigröße</li><li><strong>Zugänglichkeit</strong>: Einhaltung von Barrierefreiheitsstandards</li></ol><h2 id="verwendung" tabindex="-1">Verwendung <a class="header-anchor" href="#verwendung" aria-label="Permalink to &quot;Verwendung&quot;">​</a></h2><p>Die Komponenten des Core Systems werden automatisch geladen, wenn Sie die Hauptdatei der Casoon UI Library importieren:</p><div class="language-css vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">@import</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;@casoon/ui-lib/core.css&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">;</span></span></code></pre></div><p>Für spezifischere Anforderungen können Sie einzelne Teile des Core Systems separat importieren.</p><h2 id="weiterfuhrende-themen" tabindex="-1">Weiterführende Themen <a class="header-anchor" href="#weiterfuhrende-themen" aria-label="Permalink to &quot;Weiterführende Themen&quot;">​</a></h2><ul><li><a href="/ui-docs/core/layer-system.html">Layer System</a></li><li><a href="/ui-docs/core/css-system.html">CSS-Architektur</a></li><li><a href="/ui-docs/core/colors-system.html">Color System</a></li><li><a href="/ui-docs/core/color-mix-system.html">Color-Mix System</a></li><li><a href="/ui-docs/core/logical-properties.html">Logical Properties</a></li><li><a href="/ui-docs/core/smooth-scroll.html">Smooth Scrolling</a></li></ul>',14)]))}const g=i(s,[["render",a]]);export{u as __pageData,g as default};
