import{_ as n,c as i,o as t,ag as o}from"./chunks/framework.B6PdRehI.js";const g=JSON.parse('{"title":"Storybook Integration","description":"","frontmatter":{"title":"Storybook Integration","category":"Storybook"},"headers":[],"relativePath":"storybook/index.md","filePath":"storybook/index.md"}'),r={name:"storybook/index.md"};function s(a,e,l,d,u,k){return t(),i("div",null,e[0]||(e[0]=[o('<h1 id="storybook-integration" tabindex="-1">Storybook Integration <a class="header-anchor" href="#storybook-integration" aria-label="Permalink to &quot;Storybook Integration&quot;">​</a></h1><p>Die Casoon UI Library bietet eine nahtlose Integration mit Storybook, einem leistungsstarken Werkzeug für die Entwicklung und Dokumentation von UI-Komponenten. Diese Integration ermöglicht es, interaktive Beispiele direkt in der VitePress-Dokumentation einzubetten und ein umfassendes Entwicklungserlebnis zu schaffen.</p><h2 id="was-ist-storybook" tabindex="-1">Was ist Storybook? <a class="header-anchor" href="#was-ist-storybook" aria-label="Permalink to &quot;Was ist Storybook?&quot;">​</a></h2><p>Storybook ist eine Open-Source-Umgebung zum Entwickeln UI-Komponenten in Isolation. Es ermöglicht Entwicklern, Komponenten unabhängig von ihrer Anwendung zu erstellen, zu testen und zu dokumentieren. Die Hauptvorteile sind:</p><ul><li><strong>Isolierte Komponentenentwicklung</strong>: Erstellen und testen Sie Komponenten unabhängig von Ihrer Anwendungslogik</li><li><strong>Interaktive Dokumentation</strong>: Zeigen Sie die Komponenten in verschiedenen Zuständen und mit verschiedenen Parametern</li><li><strong>Visuelle Tests</strong>: Erkennen Sie visuell, wenn Änderungen unbeabsichtigte Auswirkungen haben</li><li><strong>Zentraler Sammelplatz</strong>: Fungiert als zentrale Anlaufstelle für Designer und Entwickler</li></ul><h2 id="storybook-in-der-casoon-ui-library" tabindex="-1">Storybook in der Casoon UI Library <a class="header-anchor" href="#storybook-in-der-casoon-ui-library" aria-label="Permalink to &quot;Storybook in der Casoon UI Library&quot;">​</a></h2><p>In der Casoon UI Library wird Storybook verwendet, um:</p><ol><li><strong>Alle Komponenten zu dokumentieren</strong>: Jede Komponente verfügt über ihre eigene Story, die ihre verschiedenen Zustände und Verwendungsmöglichkeiten zeigt</li><li><strong>Design-Tokens zu visualisieren</strong>: Farben, Typografie, Abstände und andere Designvariablen werden anschaulich dargestellt</li><li><strong>Interaktive Beispiele zu bieten</strong>: Nutzer können mit Komponenten interagieren und Eigenschaften in Echtzeit ändern</li><li><strong>Best Practices zu demonstrieren</strong>: Die Stories zeigen empfohlene Verwendungsmuster</li></ol><h2 id="zugriff-auf-das-storybook" tabindex="-1">Zugriff auf das Storybook <a class="header-anchor" href="#zugriff-auf-das-storybook" aria-label="Permalink to &quot;Zugriff auf das Storybook&quot;">​</a></h2><p>Das Storybook der Casoon UI Library ist an den folgenden Stellen verfügbar:</p><ul><li><strong>Online</strong>: <a href="https://casoon.github.io/ui-lib/storybook" target="_blank" rel="noreferrer">https://casoon.github.io/ui-lib/storybook</a></li><li><strong>Lokal</strong>: Nach der Installation der Bibliothek kann das Storybook lokal ausgeführt werden mit:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">npx</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> storybook@latest</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dev</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -p</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 6006</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -c</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> node_modules/@casoon/ui-lib/.storybook</span></span></code></pre></div><h2 id="vitepress-und-storybook-eine-leistungsstarke-kombination" tabindex="-1">VitePress und Storybook: Eine leistungsstarke Kombination <a class="header-anchor" href="#vitepress-und-storybook-eine-leistungsstarke-kombination" aria-label="Permalink to &quot;VitePress und Storybook: Eine leistungsstarke Kombination&quot;">​</a></h2><p>Die Integration von VitePress (diese Dokumentation) und Storybook bietet einen umfassenden Ansatz für die Dokumentation:</p><ul><li><strong>VitePress</strong> bietet detaillierte Erklärungen, Konzepte und Anleitungen</li><li><strong>Storybook</strong> zeigt interaktive Beispiele, Variationen und praktische Anwendungsfälle</li></ul><p>Gemeinsam bilden sie ein leistungsstarkes Referenzsystem für Entwickler, die mit der Casoon UI Library arbeiten.</p><h2 id="nachste-schritte" tabindex="-1">Nächste Schritte <a class="header-anchor" href="#nachste-schritte" aria-label="Permalink to &quot;Nächste Schritte&quot;">​</a></h2><p>Entdecken Sie die Integration von Storybook in dieser Dokumentation:</p><ul><li><a href="/ui-docs/storybook/integration.html">Integration einrichten</a> - Erfahren Sie, wie Sie Storybook in Ihren eigenen Projekten einrichten</li><li><a href="/ui-docs/storybook/component-examples.html">Komponenten-Beispiele</a> - Sehen Sie sich die Beispiele für alle UI-Komponenten an</li><li><a href="/ui-docs/storybook/embedding.html">Einbetten von Stories</a> - Lernen Sie, wie Sie Storybook-Beispiele in Ihre eigene Dokumentation einbetten können</li></ul>',19)]))}const b=n(r,[["render",s]]);export{g as __pageData,b as default};
